package com.cccloud.blogs.commons;import com.cccloud.blogs.easybatis.anno.table.Ignore;import com.cccloud.blogs.easybatis.interfaces.Page;import io.swagger.annotations.ApiModelProperty;import lombok.Data;/** * 创建人：徐卫超 * 时间：2019/6/24 16:06 * 功能： * 备注： */@Datapublic class PageRequest implements Page {    @Ignore    @ApiModelProperty(value = "记录数", example = "10")    private long pageSize = 10L;    @Ignore    @ApiModelProperty(value = "页码", example = "1")    private long pageNum = 1L;    public void setPage(Integer pageNum, Integer pageSize) {        this.pageNum = pageNum;        this.pageSize = pageSize;    }    @Override    public long getPageStart() {        if ( pageNum < 1) {            return 0;        }        return (pageNum - 1) * pageSize;    }    @Override    public long getPageOffset() {        if (pageSize < 0 || pageSize > 1000) {            return 10;        }        return pageSize;    }    public StringBuilder buildLimit() {        return new StringBuilder(" LIMIT #{limitStart}, #{limitOffset} ");    }}