package com.cccloud.blogs.config.i18n;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.MessageSource;import org.springframework.context.i18n.LocaleContextHolder;import org.springframework.stereotype.Component;import java.util.Locale;/** * 创建人：徐卫超 * 时间：2019/7/5 14:01 * 功能：国际化服务 * 备注： */@Componentpublic class I18nMessage implements I18nMessageSource {    @Autowired    private MessageSource messageSource;    public String getMessage(String code) {        try {            return messageSource.getMessage(code, null, getlocale());        } catch (Exception e) {            return code;        }    }    public String getMessage(Exception e) {        try {            return messageSource.getMessage(e.getMessage(), null, getlocale());        } catch (Exception ex) {            return e.getMessage();        }    }    public Locale getlocale() {        return LocaleContextHolder.getLocale();    }}