package com.cccloud.blogs.controller.blogs;import com.cccloud.blogs.commons.JsonMessage;import com.cccloud.blogs.commons.PageResponse;import com.cccloud.blogs.controller.blogs.article.ArticleDto;import com.cccloud.blogs.controller.blogs.article.ArticleFilterDto;import com.cccloud.blogs.service.blogs.ArticleService;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;import java.util.stream.Collectors;/** * 创建人：徐卫超 * 时间：2019/7/12 14:43 * 功能： * 备注： */@RestController@RequestMapping("/article")@Api(tags = "BLOGS_文章描述管理")public class ArticleController {    @Autowired    private ArticleService articleService;    @GetMapping("")    @ApiOperation("组列表")    public JsonMessage<PageResponse<ArticleDto>> list(ArticleFilterDto filter) {        List<ArticleDto> groupList = articleService.list(filter).stream().map(ArticleDto::convert).collect(Collectors.toList());        Long count = articleService.count(filter);        return JsonMessage.succeed(PageResponse.init(groupList, count, filter.getPageSize()));    }}