package com.cccloud.blogs.easybatis.interfaces.impl;import com.cccloud.blogs.easybatis.anno.table.Ignore;import com.cccloud.blogs.easybatis.interfaces.Page;/** * 创建人：徐卫超 * 时间：2019/10/16 14:57 * 功能： * 备注： */public class DefaultPage implements Page {    @Ignore    private long pageNum = 1;    @Ignore    private long pageSize = 10;    public void setPage(Integer pageNum, Integer pageSize) {        this.pageNum = pageNum;        this.pageSize = pageSize;    }    @Override    public long getPageStart() {        if ( pageNum < 1) {            return 0;        }        return (pageNum - 1) * pageSize;    }    @Override    public long getPageOffset() {        if (pageSize < 0 || pageSize > 1000) {            return 10;        }        return pageSize;    }}